<?php//// Базовый контроллер сайта.//abstract class C_Base extends C_Controller{	protected $title;		// заголовок страницы	protected $content;		// содержание страницы			protected function before()	{		$this->title = 'Название сайта';		$this->content = '';                                //запоминание посещаемых страниц                                if (isset($_SESSION['login'])) {                                    $pages = [];                                    if (isset($_SESSION['pages'])) {                                        $pages = $_SESSION['pages'];                                    }                                    $pages[] = trim(substr(                                            $_SERVER['REQUEST_URI'],                                             strpos($_SERVER['REQUEST_URI'], 'index')                                    ));                                    if (count($pages) > 5) {                                        array_shift($pages);                                    }                                    $_SESSION['pages'] = $pages;                                }	}		//	// Генерация базового шаблона	//		public function render()	{		$vars = array('title' => $this->title, 'content' => $this->content);			$page = $this->Template('v/v_main.php', $vars);						echo $page;	}	}