<?php//// Конттроллер страницы чтения.//include_once('m/M_User.php');class C_User extends C_Base{	    public function action_auth(){        $message;        if ($_POST) {            $user = new M_User;            if ($user->checkUser($_POST['login'], $_POST['password'])) {                $_SESSION['login'] = $_POST['login'];                header('Location: index.php');            } else {                $message = 'Неверное имя пользователя или пароль!';            }        }        $this->title .= '::Вход';        $this->content = $this->Template('v/v_auth.php', array('message' => $message));    }        public function action_logout() {        if (isset($_SESSION['login'])) {            unset($_SESSION['login']);        }        header('Location: index.php');    }        public function action_cabinet() {        if (!isset($_SESSION['login'])) {            header('Location: index.php');        }        $pages = [];        if (isset($_SESSION['pages'])) {            $pages = $_SESSION['pages'];        }        $this->title .= '::Личный кабинет';        $this->content = $this->Template(                'v/v_cabinet.php',                 array('user' => $_SESSION['login'], 'pages' => $pages)        );    }}